import { useEffect, useState } from 'react';
import { useCookies } from 'react-cookie';
import axios from 'axios';

const MyPlaces = ({ saved_places }) => {
  const [cookies] = useCookies(['userId']);
  const [savedPlaces, setSavedPlaces] = useState([]);
  const userId = cookies.UserId;

  // const fetchSavedPlaces = async () => {
  //   try {
  //     const response = await axios.get(`http://localhost:8000/save-places?userId=${userId}`);
  //     setSavedPlaces(response.data);
  //   } catch (error) {
  //     console.log(error);
  //   }
  // };

  // useEffect(() => {
  //   fetchSavedPlaces();
  // }, [userId]);

  const fetchSavedPlaces = async () => {
    try {
      const promises = savedPlaces.map(async (placeId) => {
        try {
          const response = await axios.get(`http://localhost:8000/save-places/${placeId}`);
          return response.data; // Assuming the endpoint to fetch individual place details is '/save-places/:id'
        } catch (error) {
          console.log(error);
        }
      });

      // Wait for all API calls to finish
      const placeDetails = await Promise.all(promises);

      // Merge the complete place objects with the existing savedPlaces array
      const updatedSavedPlaces = savedPlaces.map((placeId) =>
        placeDetails.find((place) => place._id === placeId)
      )

      setSavedPlaces(updatedSavedPlaces);
    } catch (error) {
      console.log(error);
    }
  }

  useEffect(() => {
    fetchSavedPlaces()
  }, [userId])



  console.log(savedPlaces)

  return (
    <div>
      <h1>List of Saved Places</h1>
        <ul>
        {savedPlaces.map((place) => (
          <li key={place._id}>
            <h3>{place.place_name}</h3>
            <p>{place.place_street}</p>
            <p>
              {place.place_city}{', '}{place.place_state}{' '}{place.place_zip}
            </p>
            <a href={place.place_url}>Website</a>
            <p>{place.place_about}</p>
          </li>
        ))}
        </ul>
    </div>
  );
};

export default MyPlaces;
