import { useEffect, useState } from 'react';
import { useCookies } from 'react-cookie';
import axios from 'axios';

const MyPlaces = () => {
  const [cookies] = useCookies(['userId']);
  const [user, setUser] = useState(null);
  const [savedPlaces, setSavedPlaces] = useState([]);

  useEffect(() => {
    // Fetch logged-in user's data from backend API
    const fetchUserData = async () => {
      try {
        const response = await axios.get(`http://localhost:8000/user?userId=${cookies.userId}`);
        setUser(response.data);
        setSavedPlaces(response.data.saved_places || []);
      } catch (error) {
        console.log(error);
      }
    };

    fetchUserData();
  }, [cookies.userId]);

  // Fetch full place details for each saved place ID
  useEffect(() => {
    const fetchSavedPlacesDetails = async () => {
      const promises = savedPlaces.map(async (placeId) => {
        try {
          const response = await axios.get(`http://localhost:8000/places/${placeId}`);
          return response.data; // Assuming the endpoint to fetch individual place details is '/places/:id'
        } catch (error) {
          console.log(error);
        }
      });

      // Wait for all API calls to finish and set the full place objects in state
      const placeDetails = await Promise.all(promises);
      setSavedPlaces(placeDetails);
    };

    fetchSavedPlacesDetails();
  }, [savedPlaces]);

  return (
    <div>
      <h1>List of Saved Places</h1>
      {savedPlaces.map((place) => (
        <div key={place._id}>
          <h3>{place.place_name}</h3>
          <p>{place.place_street}</p>
          <p>
            {place.place_city}, {place.place_state} {place.place_zip}
          </p>
          <a href={place.place_url}>Website</a>
          <p>{place.place_about}</p>
        </div>
      ))}
    </div>
  );
};

export default MyPlaces;
