import TinderCard from 'react-tinder-card'
import {useEffect, useState} from 'react'
import {useCookies} from 'react-cookie'
import Nav from '../components/Nav'
import ChatContainer from '../components/ChatContainer'
import MatchesDisplay from '../components/MatchesDisplay'
import ProfileContainer from '../components/ProfileContainer'
import axios from 'axios'
import CalculatedAge from '../components/CalculatedAge'

const MyMatches = () => {
    const [user, setUser] = useState(null)
    const [genderedUsers, setGenderedUsers] = useState(null)
    const [lastDirection, setLastDirection] = useState()
    const [cookies, setCookie, removeCookie] = useCookies(['user'])

    const userId = cookies.UserId

    const getUser = async () => {
        try {
            const response = await axios.get('http://localhost:8000/user', {
                params: {userId}
            })
            setUser(response.data)
            console.log(response.data)
        } catch (error) {
            console.log(error)
        }
    }

    const getGenderedUsers = async () => {
        try {
            const response = await axios.get('http://localhost:8000/mutual-gender-matches', {
                params: {gender: user?.gender_identity, gender_interest: user?.gender_interest}
            })
            setGenderedUsers(response.data)
        } catch (error) {
            console.log(error)
        }
    }

    useEffect(() => {
        getUser()

    }, [])

    useEffect(() => {
        if (user) {
            getGenderedUsers()
        }
    }, [user])

    const updateMatches = async (matchedUserId) => {
        try {
            await axios.put('http://localhost:8000/addmatch', {
                userId,
                matchedUserId
            })
            getUser()
        } catch (error) {
            console.log(error)
        }
    }

    console.log(user)



    const swiped = (direction, swipedUserId) => {
      if (direction === 'right') {
        updateMatches(swipedUserId)
      }
      setLastDirection(direction)
    }

    const outOfFrame = (name) => {
      console.log(name + ' left the screen!')
    }

    const matchedUserIds = user?.matches.map(({user_id}) => user_id).concat(userId)

    const filteredGenderedUsers = genderedUsers?.filter(genderedUser => !matchedUserIds.includes(genderedUser.user_id))

    return (
        <>
        { user && 
            <>
            <Nav
                minimal={true}
                setShowModal={() => { } }
                showModal={false} />
            <ProfileContainer user={user}/>
            <div className="swipe-container">
                <h3>My New Matches</h3>
               <div className="card-container">
                    {filteredGenderedUsers && filteredGenderedUsers.length > 0 ? (
                        filteredGenderedUsers.map((genderedUser) => (
                        <TinderCard 
                            className="swipe" 
                            key={genderedUser.user_id} 
                            onSwipe={(dir) => swiped(dir, genderedUser.user_id)} 
                            onCardLeftScreen={() => outOfFrame(genderedUser.first_name)}>
                            <div 
                                style={{ backgroundImage: 'url(' + genderedUser.url + ')' }} 
                                className="card">
                                <div className="card match-info">
                                    <h2>
                                        {genderedUser.first_name},{' '}
                                        <CalculatedAge 
                                        dob_year={genderedUser.dob_year} 
                                        dob_month={genderedUser.dob_month} 
                                        dob_day={genderedUser.dob_day} 
                                        />
                                    </h2>
                                    <h3>"{genderedUser.about}"</h3>
                                </div>
                            </div>
                        </TinderCard>
                     ))
                    ) : (
                        <p>You have no new matches.</p>
                    )}
                     <div className="swipe-info">
                         {lastDirection ? <p>You swiped {lastDirection}</p> : <p/>}
                    </div>
                </div>
             </div>
            </>
        }
        </>
    )

}

export default MyMatches
