import ProfileHeader from './ProfileHeader';
import MatchesDisplay from './MatchesDisplay';
import ProfileDisplay from './ProfileDisplay';
import { useState } from 'react';

const ProfileContainer = ({ user }) => {
  const [clickedUser, setClickedUser] = useState(null);

  // Function to handle the click event and update the clickedUser state
  const handleUserClick = (clickedUser) => {
    setClickedUser(clickedUser);
  };

  return (
    <div className="chat-container">
      <ProfileHeader user={user} />

      <div>
        {/* Set clickedUser to null when "Matches" button is clicked */}
        <button className="option" onClick={() => setClickedUser(null)}>
          Matches
        </button>
        <button className="option" disabled={!clickedUser}>
          Profile
        </button>
      </div>

      {/* Pass the handleUserClick function to the MatchesDisplay component */}
      {!clickedUser && (
        <MatchesDisplay matches={user.matches} onUserClick={handleUserClick} />
      )}

      {clickedUser && <ProfileDisplay user={user} clickedUser={clickedUser} />}
    </div>
  );
};

export default ProfileContainer;
