import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {useCookies} from 'react-cookie'

const PlaceList = () => {
  const [placesByMetro, setPlacesByMetro] = useState([]);
  const [cookies, setCookie, removeCookie] = useCookies(null)

  useEffect(() => {
    // Fetch data from the backend API
    const fetchPlaces = async () => {
      try {
        const response = await axios.get('http://localhost:8000/places-by-metro'); // Replace with your backend API endpoint to get places grouped by metro area
        setPlacesByMetro(response.data);
      } catch (error) {
        console.log(error);
      }
    };

    fetchPlaces();
  }, []);

  const [savedPlaces, setSavedPlaces] = useState([]);

  //function to toggle saved status of a place
  const toggleSavedStatus = async (placeId) => {
    setSavedPlaces((prevSavedPlaces) => {
      if (savedPlaces.includes(placeId)) {
        return prevSavedPlaces.filter((id) => id !== placeId);
      } else {
        return [...prevSavedPlaces, placeId];
      }
    });

    try {
      // Make a POST or PUT request to your backend API to update the user's saved places
      // Replace 'http://localhost:8000/update-saved-places' with your actual backend API endpoint
      await axios.post('http://localhost:8000/save-places', {
        userId: cookies.UserId, // Assuming you have the user ID available in cookies
        savedPlaces: savedPlaces.includes(placeId) ? savedPlaces.filter((id) => id !== placeId) : [...savedPlaces, placeId]
      });
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div>
      <h1>List of Places</h1>
      {placesByMetro.map((metro) => (
        <div key={metro._id}>
          <h2>{metro.place_metro}</h2>
          <ul>
            {metro.places.map((place) => (
              <li key={place._id}>
                <h3>{place.place_name}</h3>
                <p>{place.place_street}</p>
                <p>{place.place_city}{', '}{place.place_state}{' '}{place.place_zip}</p>
                <a href={place.place_url}>Website</a>
                <p>{place.place_about}</p>
                <span
                  onClick={() => toggleSavedStatus(place._id)}
                  style={{ cursor: 'pointer', color: savedPlaces.includes(place._id) ? 'red' : 'gray ' }}
                >
                  ❤️
                </span>
                {savedPlaces.includes(place._id) ? <span>Saved to My Places</span> : <span>Save to My Places</span>}
              </li>
            ))}
          </ul>
        </div>
      ))}
    </div>
  );
};

export default PlaceList;
